mapper.select:

select *
from db_house.collection
where user_id = #{user_id}
  and house_id = #{house_id}

mapper.delete:
delete
from db_house.collection
where user_id = #{user_id}
  and house_id = #{house_id}

mapper.update:
update db_house.house
set click_num=click_num + 1
where house_id = #{house_id}
